import { thisExpression } from "@babel/types";
import React from "react";
import {
    InputGroup,
    FormControl,
    Button,
    Form,
    Col,
    Row,
    Accordion,
} from "react-bootstrap";
import BoolExpression from "../scripts/BooleanSimplifier/BoolExpression";

class ExpressionForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { expValue: "", isInvalid: false, errorMsg: "Invalid Expression" };
        this.expression = null;
    }

    handleChange = (event) => {
        this.setState({ expValue: event.target.value });
    };

    handleSubmit = (event) => {
        try {
            this.expression = BoolExpression.ExpressionInstance(
                this.state.expValue,
                true
            );
            this.setState({ isInvalid: false }, () => {
                this.props.displayExpression(this.expression);
            });
        } catch (error) {
            event.preventDefault();
            event.stopPropagation();
            this.setState({ isInvalid: true, errorMsg: "Error: " + error + "!" });
        }
    };

    render() {
        return (
            <>
                <Form noValidate validated={this.state.validated}>
                    <Row>
                        <Form.Group as={Col} controlId="expressionForm">
                            <InputGroup hasValidation>
                                <FormControl
                                    required
                                    placeholder="Enter Expression"
                                    aria-label="Enter Expression"
                                    aria-describedby="basic-addon"
                                    onChange={this.handleChange}
                                    isInvalid={this.state.isInvalid}
                                />
                                <Button
                                    variant="secondary"
                                    id="button-addon"
                                    onClick={this.handleSubmit}
                                >
                                    Simplify
                                </Button>
                                <Form.Control.Feedback type="invalid">
                                    {this.state.errorMsg}
                                </Form.Control.Feedback>
                            </InputGroup>
                        </Form.Group>
                    </Row>
                </Form>
                <Row>
                    <Accordion flush>
                        <Accordion.Item eventKey="0">
                            <Accordion.Header>
                                {"Operator Symbols "} <b>/</b> {"Examples "}
                                <b>/</b> {"About "}
                            </Accordion.Header>

                            <Accordion.Body>
                                Lorem ipsum dolor sit amet, consectetur adipiscing elit,
                                sed do eiusmod tempor incididunt ut labore et dolore magna
                                aliqua. Ut enim ad minim veniam, quis nostrud exercitation
                                ullamco laboris nisi ut aliquip ex ea commodo consequat.
                                Duis aute irure dolor in reprehenderit in voluptate velit
                                esse cillum dolore eu fugiat nulla pariatur. Excepteur
                                sint occaecat cupidatat non proident, sunt in culpa qui
                                officia deserunt mollit anim id est laborum.
                            </Accordion.Body>
                        </Accordion.Item>
                    </Accordion>
                </Row>
            </>
        );
    }
}

export default ExpressionForm;
