import { thisExpression } from "@babel/types";
import React from "react";
import {
    InputGroup,
    FormControl,
    Button,
    Form,
    Col,
    Row,
    Accordion,
} from "react-bootstrap";
import BoolExpression from "../scripts/BooleanSimplifier/BoolExpression";

class ExpressionForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { expValue: "", isInvalid: false, errorMsg: "Invalid Expression" };
        this.expression = null;
    }

    handleChange = (event) => {
        this.setState({ expValue: event.target.value });
    };

    handleSubmit = (event) => {
        alert("Expression submitted: " + this.state.expValue);

        try {
            this.expression = BoolExpression.ExpressionInstance(
                this.state.expValue,
                true
            );
            this.setState({ isInvalid: false });
        } catch (error) {
            event.preventDefault();
            event.stopPropagation();
            this.setState({ isInvalid: true, errorMsg: "Error: " + error + "!" });
        }
    };

    render() {
        return (
            <>
                <Form
                    noValidate
                    validated={this.state.validated}
                    onSubmit={this.handleSubmit}
                >
                    <Row>
                        <Form.Group as={Col} controlId="expressionForm">
                            <InputGroup hasValidation>
                                <FormControl
                                    required
                                    placeholder="Enter Expression"
                                    aria-label="Enter Expression"
                                    aria-describedby="basic-addon"
                                    onChange={this.handleChange}
                                    isInvalid={this.state.isInvalid}
                                />
                                <Button
                                    variant="outline-secondary"
                                    id="button-addon"
                                    type="submit"
                                >
                                    Simplify
                                </Button>
                                <Form.Control.Feedback type="invalid">
                                    {this.state.errorMsg}
                                </Form.Control.Feedback>
                            </InputGroup>
                        </Form.Group>
                    </Row>
                </Form>
                <Accordion defaultActiveKey="0" flush>
                    <Accordion.Item eventKey="0">
                        <Accordion.Header>Accordion Item #1</Accordion.Header>
                        <Accordion.Body>
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                            do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                            Ut enim ad minim veniam, quis nostrud exercitation ullamco
                            laboris nisi ut aliquip ex ea commodo consequat. Duis aute
                            irure dolor in reprehenderit in voluptate velit esse cillum
                            dolore eu fugiat nulla pariatur. Excepteur sint occaecat
                            cupidatat non proident, sunt in culpa qui officia deserunt
                            mollit anim id est laborum.
                        </Accordion.Body>
                    </Accordion.Item>
                    <Accordion.Item eventKey="1">
                        <Accordion.Header>Accordion Item #2</Accordion.Header>
                        <Accordion.Body>
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                            do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                            Ut enim ad minim veniam, quis nostrud exercitation ullamco
                            laboris nisi ut aliquip ex ea commodo consequat. Duis aute
                            irure dolor in reprehenderit in voluptate velit esse cillum
                            dolore eu fugiat nulla pariatur. Excepteur sint occaecat
                            cupidatat non proident, sunt in culpa qui officia deserunt
                            mollit anim id est laborum.
                        </Accordion.Body>
                    </Accordion.Item>
                </Accordion>
            </>
        );
    }
}

export default ExpressionForm;
