import logo from "./logo.svg";
import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import ExpressionForm from "./components/ExpressionForm";
import { Container, Row, Col, Card, Badge } from "react-bootstrap";
import QMCard from "./components/QMCard";
import QMLog from "./components/QMLog";
import SOPCard from "./components/SOPCard";

class App extends React.Component {
    state = { expression: null };

    displayExpression = (expression) => {
        this.setState({ expression: expression });
    };

    render() {
        //return <ExpressionForm displayExpression={this.displayExpression} />;
        return (
            <Container>
                <div className="py-5">
                    <h2>Boolean Algebra Simplifier</h2>
                    <ExpressionForm displayExpression={this.displayExpression} />
                </div>
                {this.state.expression !== null && (
                    <>
                        <Row>
                            <Col>
                                <SOPCard
                                    expression={this.state.expression}
                                    title="Minimum Sum-Of-Products Solution"
                                />
                            </Col>
                        </Row>
                        <Row>
                            <Col>
                            </Col>
                        </Row>
                        <Row xs={1} md={3}>
                            <QMLog expression={this.state.expression} className="mt-5" />
                        </Row>
                        <Row>
                            <Col md={8}>1 of 2</Col>
                            <Col md={4}>2 of 2</Col>
                        </Row>
                    </>
                )}
            </Container>
        );
    }
}

export default App;
