import { thisExpression } from "@babel/types";
import React from "react";
import { InputGroup, FormControl, Button, Form } from "react-bootstrap";
import BoolExpression from "../scripts/BooleanSimplifier/BoolExpression";

class ExpressionForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { expValue: "", isInvalid: false, errorMsg: "Invalid Expression" };
        expression = null;
    }

    handleChange = (event) => {
        this.setState({ expValue: event.target.value });
    };

    handleSubmit = (event) => {
        alert("Expression submitted: " + this.state.expValue);

        try {
            this.props.expression = BoolExpression.ExpressionInstance(
                this.state.expValue,
                true
            );
            this.setState({ isInvalid: false });
        } catch (error) {
            event.preventDefault();
            event.stopPropagation();
            this.setState({ isInvalid: true, errorMsg: "Invalid Expression: " + error });
        }
    };

    render() {
        return (
            <Form
                noValidate
                validated={this.state.validated}
                onSubmit={this.handleSubmit}
            >
                <Form.Group>
                    <InputGroup className="mb-3" hasValidation>
                        <FormControl
                            required
                            placeholder="Enter Expression"
                            aria-label="Enter Expression"
                            aria-describedby="basic-addon2"
                            onChange={this.handleChange}
                            isInvalid={this.state.isInvalid}
                        />
                        <Button
                            variant="outline-secondary"
                            id="button-addon2"
                            type="submit"
                        >
                            Simplify
                        </Button>
                        <Form.Control.Feedback type="invalid">
                            {this.state.errorMsg}
                        </Form.Control.Feedback>
                    </InputGroup>
                </Form.Group>
            </Form>
        );
    }
}

export default ExpressionForm;
