import { Badge, Card, Row } from "react-bootstrap";
import QM from "../scripts/BooleanSimplifier/QMFunctions";
import BitSet from "bitset";

export default function QMCard(props) {
    let it = 0;
    let end = props.qmterms.length - 1;
    return (
        <Card>
            <Card.Header>{props.header}</Card.Header>
            <Card.Body>
                {props.qmterms.map((term) => {
                    return (
                        <>
                            <Row className="justify-content-end mb-1 mt-1">
                                <div className="mr-auto">
                                    {"("}
                                    {QM.extractMinTerms(term).map((minTerm) => (
                                        <Badge pill bg="info">
                                            {minTerm}
                                        </Badge>
                                    ))}
                                    {")"}
                                </div>
                                <div className="d-inline">
                                    <Badge bg="success" className="ml-1">
                                        {QM.toString(term, props.varCount)}
                                    </Badge>
                                </div>
                            </Row>
                            {it++ !== end && <hr className="m-0" />}
                        </>
                    );
                })}
            </Card.Body>
        </Card>
    );
}
